import React, { useState } from 'react';
import '../stylesheets/Shop.css';

function Shop() {
	const [stateMin, setStateMin] = useState(0);
	const [stateMax, setStateMax] = useState(0);
	const [stateInput, setStateInput] = useState(stateMin);
	function checkedPriceMin(event){
		setStateMin(event.target.value)
	}
	function checkedPriceMax(event){
		setStateMax(event.target.value)
	}
	function checkedPriceInput(event){
		setStateInput(event.target.value)
	}
	function HidePrice(){
		const price = document.querySelector('.priceSelector')
		price.ad
	}
	return (
		<div className='AllShop'>
			<div className='FiltrBlock'>
				<div className='infoS'>
					<p>Разделочные ножи</p>
					<a href='#'>Главная</a>
					<i class="fa-solid fa-chevron-right"></i>
					<a href='#'>Разделочные ножи</a>
				</div>
				<div className='Filtr'>
					<div className='FitrT'>
						<p>Фильтр товаров</p>
					</div>
					<div className='Price'>
						<div className='PriceText'>
							<p>Цена</p>
							<i onClick={HidePrice} class="fa-solid fa-chevron-up"></i>
						</div>
						<div className='priceSelector'>
							<div className='priceSelect'>
								{/* <span>{stateMin}</span>
								<span>{stateMax}</span> */}
								<input
									onChange={checkedPriceInput} 
									value={stateInput}
								/>
							</div>
							<input 
								type="range" 
								min="0" 
								max="5000" 
								step="1"
								onChange={checkedPriceMin} 
							/>
							<input 
								type="range" 
								min="0" 
								max="5000" 
								step="1"
								onChange={checkedPriceMax} 
							/>
						</div>
					</div>

				</div>
			</div>
			<div className='MainShop'></div>
		</div>
	)
}

export default Shop
